      out.write("	<script>\r\n");
      out.write("	const today = new Date().toISOString().split('T')[0];\r\n");
      out.write("    document.getElementById(\"checkIn\").value = today;\r\n");
      out.write("    document.getElementById(\"checkOut\").value = today;\r\n");
      out.write("    document.getElementById('checkIn').setAttribute('min', today);\r\n");
      out.write("    document.getElementById('checkOut').setAttribute('min', today);\r\n");
      out.write("	const pricePerNight = ");
      out.print(formattedPrice);
      out.write(";\r\n");
      out.write("\r\n");
      out.write("	function formatCurrency(amount) {\r\n");
      out.write("	    return \"₹\" + amount;\r\n");
      out.write("	}\r\n");
      out.write("\r\n");
      out.write("	function calculateTotal() {\r\n");
      out.write("\r\n");
      out.write("		\r\n");
      out.write("	    \r\n");
      out.write("	    const checkInDate = new Date(document.getElementById(\"checkIn\").value);\r\n");
      out.write("	    const checkOutDate = new Date(document.getElementById(\"checkOut\").value);\r\n");
      out.write("	    \r\n");
      out.write("	    if (checkInDate >= checkOutDate) {\r\n");
      out.write("	        return; \r\n");
      out.write("	    }\r\n");
      out.write("	    \r\n");
      out.write("	    const nights = Math.ceil((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24));\r\n");
      out.write("	    \r\n");
      out.write("	    const roomCharges = nights * pricePerNight;\r\n");
      out.write("	    \r\n");
      out.write("	    const taxes = roomCharges * 0.18;\r\n");
      out.write("	    \r\n");
      out.write("	    const services = Array.from(document.querySelectorAll('.form-check-input:checked'));\r\n");
      out.write("	    let serviceCost = services.reduce((total, service) => {\r\n");
      out.write("	        return total + parseFloat(service.dataset.price || 0);\r\n");
      out.write("	    }, 0);\r\n");
      out.write("	    \r\n");
      out.write("	    if (document.getElementById(\"breakfast\").checked) {\r\n");
      out.write("	        const adults = parseInt(document.getElementById(\"adults\").value) || 0;\r\n");
      out.write("	        const children = parseInt(document.getElementById(\"children\").value) || 0;\r\n");
      out.write("	        const totalGuests = adults + children;\r\n");
      out.write("	        serviceCost += (500 * totalGuests * nights) - 500; \r\n");
      out.write("	    }\r\n");
      out.write("	    \r\n");
      out.write("	    const total = roomCharges + taxes + serviceCost;\r\n");
      out.write("	    \r\n");
      out.write("	    document.getElementById(\"roomCharges\").textContent = formatCurrency(roomCharges);\r\n");
      out.write("	    document.getElementById(\"taxes\").textContent = formatCurrency(taxes);\r\n");
      out.write("	    document.getElementById(\"additionalServices\").textContent = formatCurrency(serviceCost);\r\n");
      out.write("	    document.getElementById(\"totalAmount\").textContent = formatCurrency(total);\r\n");
      out.write("	    document.getElementById(\"hiddenTotalAmount\").value = total;\r\n");
      out.write("	}\r\n");
      out.write("\r\n");
      out.write("	function setupEventListeners() {\r\n");
      out.write("	    document.getElementById(\"checkIn\").addEventListener(\"change\", calculateTotal);\r\n");
      out.write("	    document.getElementById(\"checkOut\").addEventListener(\"change\", calculateTotal);\r\n");
      out.write("	    \r\n");
      out.write("	    document.getElementById(\"adults\").addEventListener(\"change\", calculateTotal);\r\n");
      out.write("	    document.getElementById(\"children\").addEventListener(\"change\", calculateTotal);\r\n");
      out.write("	    \r\n");
      out.write("	    const services = document.querySelectorAll('.form-check-input');\r\n");
      out.write("	    services.forEach(service => {\r\n");
      out.write("	        service.addEventListener(\"change\", calculateTotal);\r\n");
      out.write("	    });\r\n");
      out.write("	}\r\n");
      out.write("\r\n");
      out.write("	document.addEventListener(\"DOMContentLoaded\", () => {\r\n");
