      out.write("	    document.getElementById(\"totalAmount\").textContent = formatCurrency(0);\r\n");
      out.write("	    document.getElementById(\"hiddenTotalAmount\").value = total;\r\n");
      out.write("	    document.getElementById(\"hiddenBreakfast\").value = document.getElementById(\"breakfast\").checked;\r\n");
      out.write("	    document.getElementById(\"hiddenAirportTransfer\").value = document.getElementById(\"airport\").checked;\r\n");
      out.write("	    document.getElementById(\"hiddenEarlyCheckin\").value = document.getElementById(\"earlyCheckin\").checked;\r\n");
      out.write("	});\r\n");
      out.write("</script>\r\n");
